mapper.select:
SELECT foodrecomno,frecom, goalsno, mhno,rdate
FROM foodrecom
WHERE foodrecomno = #{foodrecomno}

SELECT foodrecomno, frecom, goalsno, mhno, rdate FROM ( SELECT foodrecomno, frecom, goalsno, mhno, rdate, rownum AS r FROM ( SELECT foodrecomno, frecom, goalsno, mhno, rdate FROM foodrecom
UPPER(frecom) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY foodrecomno ASC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
